import { useState, useEffect, useRef } from 'react'
// Corrected import: useShifts is defined in its own hook file, not exported from ShiftContext
import { useShifts } from '../contexts/useShifts'

const DEFAULT_AUTOSAVE_INTERVAL = 30000 // 30 seconds
const DEFAULT_MAX_SNAPSHOTS = 10 // Keep last 10 snapshots

/**
 * AutosaveManager
 * Responsibilities:
 *  - Periodically snapshot relevant shift/application/notification state to localStorage (ring buffer limited)
 *  - Offer recovery UI when unsaved work + recent snapshot present
 *  - Export & housekeeping (cleanup oldest) actions
 * Known limitations (acceptable for now):
 *  - Snapshot diffing heuristic is simplistic (signature by lengths); could hash payload for deeper dedupe.
 */
export default function AutosaveManager({
  intervalMs = DEFAULT_AUTOSAVE_INTERVAL,
  maxSnapshots = DEFAULT_MAX_SNAPSHOTS,
  enableLogging = false,
  dedupeSnapshots = false, // if true, identical (by simple signature) consecutive snapshots are skipped
} = {}) {
  const { state } = useShifts()
  const [lastSave, setLastSave] = useState(null)
  const [isRecovering, setIsRecovering] = useState(false)
  const [availableSnapshots, setAvailableSnapshots] = useState([])
  const [showRecoveryPanel, setShowRecoveryPanel] = useState(false)
  const lastSignatureRef = useRef(null)

  // Auto-save functionality
  useEffect(() => {
    const saveSnapshot = () => {
      try {
        // Build a simple signature to avoid redundant identical snapshots
        const signature = [
          state.shifts.length,
          state.applications.length,
          state.notifications.length,
          state.lastActivity || 0,
        ].join(':')
        if (dedupeSnapshots) {
          if (signature === lastSignatureRef.current) return
          lastSignatureRef.current = signature
        }

        const now = Date.now()
        const dataSource = typeof state.dataSource === 'string'
          ? state.dataSource
          : (state.dataSource?.source || 'localStorage')

        const snapshot = {
          id: now,
          timestamp: new Date(now).toISOString(),
          data: {
            shifts: state.shifts,
            applications: state.applications,
            notifications: state.notifications,
            lastActivity: state.lastActivity,
          },
          dataSource,
          changeCount: state.shifts.length + state.applications.length,
        }

        const existingSnapshots = JSON.parse(localStorage.getItem('swaxi-autosave-snapshots') || '[]')
        const updatedSnapshots = [snapshot, ...existingSnapshots].slice(0, maxSnapshots)

        localStorage.setItem('swaxi-autosave-snapshots', JSON.stringify(updatedSnapshots))
        localStorage.setItem('swaxi-last-autosave', JSON.stringify({
          timestamp: snapshot.timestamp,
          changeCount: snapshot.changeCount,
        }))

        setLastSave(new Date(snapshot.timestamp))
        setAvailableSnapshots(updatedSnapshots)
        if (enableLogging) console.log('üì∏ Autosave: Snapshot erstellt', snapshot.id)
      } catch (error) {
        if (enableLogging) console.error('‚ùå Autosave Fehler:', error)
      }
    }

    // Initial save (after mount / state updates)
    saveSnapshot()
    const interval = setInterval(saveSnapshot, intervalMs)
    return () => clearInterval(interval)
  }, [state.shifts, state.applications, state.notifications, state.dataSource, state.lastActivity, intervalMs, maxSnapshots, enableLogging, dedupeSnapshots])

  // Load snapshots on component mount
  useEffect(() => {
    try {
      const snapshots = JSON.parse(localStorage.getItem('swaxi-autosave-snapshots') || '[]');
      setAvailableSnapshots(snapshots);

      const lastSaveInfo = JSON.parse(localStorage.getItem('swaxi-last-autosave') || 'null');
      if (lastSaveInfo) {
        setLastSave(new Date(lastSaveInfo.timestamp));
      }
    } catch (error) {
      console.error('Error loading snapshots:', error);
    }
  }, []);

  // Check for recovery on app start
  useEffect(() => {
    const checkForRecovery = () => {
      try {
        const hasUnsavedWork = localStorage.getItem('swaxi-unsaved-work');
        const snapshots = JSON.parse(localStorage.getItem('swaxi-autosave-snapshots') || '[]');

        if (hasUnsavedWork && snapshots.length > 0) {
          const lastSnapshot = snapshots[0];
          const timeSinceLastSnapshot = Date.now() - new Date(lastSnapshot.timestamp).getTime();

          // If last snapshot is recent (< 5 minutes), offer recovery
          if (timeSinceLastSnapshot < 300000) {
            setShowRecoveryPanel(true);
          }
        }
      } catch (error) {
        console.error('Error checking for recovery:', error);
      }
    };

    // Check after a short delay to let the app initialize
    setTimeout(checkForRecovery, 1000);
  }, []);

  const recoverFromSnapshot = async (snapshotId) => {
    try {
      setIsRecovering(true);
      const snapshot = availableSnapshots.find(s => s.id === snapshotId);

      if (!snapshot) {
        throw new Error('Snapshot nicht gefunden');
      }

      // Here you would dispatch actions to restore the state
      // For demo purposes, we'll just show a success message
      console.log('üîÑ Wiederherstellung von Snapshot:', snapshot);

      // Simulate recovery process
      await new Promise(resolve => setTimeout(resolve, 1500));

      setShowRecoveryPanel(false);
      setIsRecovering(false);

      // Clear unsaved work flag
      localStorage.removeItem('swaxi-unsaved-work');

      alert(`‚úÖ Daten erfolgreich wiederhergestellt!\n\nSnapshot vom: ${new Date(snapshot.timestamp).toLocaleString('de-DE')}\nDatenquelle: ${snapshot.dataSource}`);
    } catch (error) {
      console.error('‚ùå Wiederherstellung fehlgeschlagen:', error);
      alert('‚ùå Wiederherstellung fehlgeschlagen: ' + error.message);
      setIsRecovering(false);
    }
  };

  const deleteSnapshot = (snapshotId) => {
    try {
      const updatedSnapshots = availableSnapshots.filter(s => s.id !== snapshotId);
      localStorage.setItem('swaxi-autosave-snapshots', JSON.stringify(updatedSnapshots));
      setAvailableSnapshots(updatedSnapshots);
    } catch (error) {
      console.error('Error deleting snapshot:', error);
    }
  };

  const exportSnapshots = () => {
    try {
      const exportData = {
        snapshots: availableSnapshots,
        exportTime: new Date().toISOString(),
        version: '6.0.0'
      };

      const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
      const url = URL.createObjectURL(blob);

      const a = document.createElement('a');
      a.href = url;
      a.download = `swaxi-snapshots-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  const formatTimeAgo = (timestamp) => {
    const now = new Date();
    const time = new Date(timestamp);
    const diffMs = now - time;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);

    if (diffMins < 1) return 'gerade eben';
    if (diffMins < 60) return `vor ${diffMins} Min`;
    if (diffHours < 24) return `vor ${diffHours} Std`;
    return time.toLocaleDateString('de-DE');
  };

  // Recovery Panel
  // Recovery overlay (keep separate so indicator still mounts for consistent layout if needed later)
  const recoveryOverlay = showRecoveryPanel && (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50" data-testid="recovery-overlay">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white">
        <div className="text-center">
          <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-yellow-100 mb-4">
            <span className="text-2xl">üîÑ</span>
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            Ungespeicherte √Ñnderungen erkannt
          </h3>
          <p className="text-sm text-gray-500 mb-4">
            Es wurden automatische Snapshots gefunden. M√∂chten Sie Ihre Arbeit wiederherstellen?
          </p>

          <div className="max-h-48 overflow-y-auto mb-4 text-left">
            {availableSnapshots.slice(0, 5).map((snapshot) => (
              <div key={snapshot.id} className="flex items-center justify-between p-3 border rounded-md mb-2">
                <div>
                  <div className="text-sm font-medium">
                    {new Date(snapshot.timestamp).toLocaleString('de-DE')}
                  </div>
                  <div className="text-xs text-gray-500">
                    {snapshot.data.shifts.length} Dienste ‚Ä¢ {snapshot.dataSource}
                  </div>
                </div>
                <button
                  onClick={() => recoverFromSnapshot(snapshot.id)}
                  disabled={isRecovering}
                  className="ml-3 inline-flex items-center px-3 py-1 border border-transparent text-xs font-medium rounded-md text-white bg-brand-primary hover:bg-brand-primary/80 disabled:opacity-50"
                >
                  {isRecovering ? 'L√§dt...' : 'Wiederherstellen'}
                </button>
              </div>
            ))}
          </div>

          <div className="flex flex-wrap justify-center gap-2 mb-4">
            <button
              onClick={() => setShowRecoveryPanel(false)}
              className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md hover:bg-gray-400"
            >
              √úberspringen
            </button>
            <button
              onClick={() => {
                localStorage.removeItem('swaxi-unsaved-work')
                setShowRecoveryPanel(false)
              }}
              className="px-4 py-2 bg-brand-primary text-white rounded-md hover:bg-brand-primary/80"
            >
              Neu beginnen
            </button>
            <button
              onClick={exportSnapshots}
              className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
            >
              Export
            </button>
            {availableSnapshots.length > 3 && (
              <button
                onClick={() => deleteSnapshot(availableSnapshots[availableSnapshots.length - 1].id)}
                className="px-4 py-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200"
              >
                Bereinigen
              </button>
            )}
          </div>
          <div className="text-xs text-gray-500">{availableSnapshots.length} Snapshots verf√ºgbar</div>
        </div>
      </div>
    </div>
  )

  // Auto-save status indicator (shown in corner)
  return (
    <>
      <div className="fixed bottom-4 right-4 z-40">
        <div className="bg-white shadow-lg rounded-lg p-3 border border-gray-200">
          <div className="flex items-center space-x-2">
            <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
            <span className="text-xs text-gray-600">
              Autosave: {lastSave ? formatTimeAgo(lastSave) : 'Initialisierung...'}
            </span>
            <button
              onClick={() => setShowRecoveryPanel(true)}
              className="text-xs text-brand-primary hover:text-brand-primary/80"
              title="Snapshots verwalten"
            >
              üì∏
            </button>
          </div>
          {availableSnapshots.length > 0 && (
            <div className="mt-2 text-xs text-gray-500">
              {availableSnapshots.length} Snapshots verf√ºgbar
            </div>
          )}
        </div>
      </div>
      {recoveryOverlay}
    </>
  )
}

import { useState } from 'react';
import { useShifts } from '../contexts/useShifts';

export default function ConnectionStatus() {
  const { state } = useShifts();
  const [testing, setTesting] = useState(false);

  const handleTestConnection = async () => {
    setTesting(true);
    try {
      // Placeholder connection test ‚Äì always false for now
      const isOnline = false;
      console.log('Connection test result:', isOnline ? 'SharePoint available' : 'SharePoint not available');
    } catch (error) {
      console.error('Connection test failed:', error);
    } finally {
      setTesting(false);
    }
  };

  const getStatusIcon = () => {
    if (state.dataSource === 'sharePoint' && state.isOnline) {
      return 'üü¢'; // Green - SharePoint connected
    } else if (state.dataSource === 'localStorage') {
      return 'üü°'; // Yellow - localStorage mode
    } else {
      return 'üî¥'; // Red - error state
    }
  };

  const getStatusText = () => {
    if (state.dataSource === 'sharePoint' && state.isOnline) {
      return 'SharePoint verbunden';
    } else if (state.dataSource === 'localStorage') {
      return 'Offline-Modus (localStorage)';
    } else {
      return 'Verbindungsfehler';
    }
  };

  return (
    <div className="flex items-center space-x-3 bg-white p-3 rounded-lg shadow-sm border">
      <div className="flex items-center space-x-2">
        <span className="text-lg">{getStatusIcon()}</span>
        <span className="text-sm font-medium text-gray-700">
          {getStatusText()}
        </span>
      </div>

      {state.lastSync && (
        <span className="text-xs text-gray-500">
          Letzte Sync: {state.lastSync.toLocaleTimeString('de-DE')}
        </span>
      )}

      <button
        onClick={handleTestConnection}
        disabled={testing}
        className="btn-secondary text-xs px-2 py-1"
      >
        {testing ? 'üîÑ Test l√§uft...' : 'üîó Verbindung testen'}
      </button>

      {/* Development Info */}
      {state.dataSource === 'localStorage' && (
        <div className="text-xs text-gray-500 bg-yellow-50 px-2 py-1 rounded">
          üí° Tipp: Funktioniert automatisch im Stadtwerke-Netzwerk
        </div>
      )}
    </div>
  );
}

import { Component } from 'react'
import { logError } from '../utils/logger'

class ErrorBoundary extends Component {
  constructor(props) {
    super(props)
    this.state = { error: null }
  }
  static getDerivedStateFromError(error) {
    return { error }
  }
  componentDidCatch(error, info) {
    const payload = {
      message: error?.message || String(error),
      stack: error?.stack,
      componentStack: info?.componentStack,
      time: new Date().toISOString(),
      version: '6.0.1'
    }
    logError('ErrorBoundary captured error', payload)
    // TODO: send to remote telemetry endpoint when available
  }
  handleReload = () => {
    window.location.reload()
  }
  render() {
    const { error } = this.state
    if (error) {
      return (
        <div role="alert" className="p-6 text-red-700 bg-red-50 rounded">
          <h2 className="font-semibold mb-2">Ein Fehler ist aufgetreten</h2>
          <pre className="text-xs whitespace-pre-wrap">{String(error.message || error)}</pre>
          <button className="mt-3 bg-brand-primary text-white px-3 py-1 rounded" onClick={this.handleReload}>Neu laden</button>
        </div>
      )
    }
    return this.props.children
  }
}

export default ErrorBoundary

import { useState, useEffect } from 'react';
// Lightweight env shim (avoids direct process reference for lint in browser context)
const __env = (typeof globalThis !== 'undefined' && globalThis.process && globalThis.process.env)
  ? globalThis.process.env
  : { NODE_ENV: 'development' };
import { useShifts } from '../contexts/useShifts';

export default function LiveVersionBanner() {
  const { state } = useShifts();
  const [isVisible, setIsVisible] = useState(true);
  const [buildInfo, setBuildInfo] = useState(null);

  useEffect(() => {
    // Get build information
    const buildTime = new Date().toLocaleString('de-DE');
    const version = '6.0.0'; // Static version for now
  // Use NODE_ENV for environment to keep Jest (CJS) compatibility instead of import.meta.env
  const environment = __env.NODE_ENV === 'production' ? 'production' : 'development';

    setBuildInfo({
      version,
      buildTime,
      environment,
      gitHash: 'latest', // Simplified for demo
      dataSource: state.dataSource?.source || 'localStorage'
    });
  }, [state.dataSource]);

  const handleDismiss = () => {
    setIsVisible(false);
    // Remember dismissal for session
    sessionStorage.setItem('bannerDismissed', 'true');
  };

  // Check if banner was previously dismissed
  useEffect(() => {
    const wasDismissed = sessionStorage.getItem('bannerDismissed');
    if (wasDismissed) {
      setIsVisible(false);
    }
  }, []);

  if (!isVisible || !buildInfo) return null;

  const getDataSourceIcon = () => {
    switch (buildInfo.dataSource) {
      case 'sharePoint':
        return '‚òÅÔ∏è';
      case 'localStorage':
        return 'üíæ';
      default:
        return 'üîÑ';
    }
  };

  const getEnvironmentBadge = () => {
    if (buildInfo.environment === 'production') {
      return (
        <span className="inline-flex items-center rounded-full bg-green-100 px-2.5 py-0.5 text-xs font-medium text-green-800">
          üöÄ Live
        </span>
      );
    }
    return (
      <span className="inline-flex items-center rounded-full bg-yellow-100 px-2.5 py-0.5 text-xs font-medium text-yellow-800">
        üß™ Demo
      </span>
    );
  };

  return (
    <div className="relative isolate flex items-center gap-x-6 overflow-hidden bg-gradient-to-r from-brand-primary to-brand-secondary px-6 py-2.5 sm:px-3.5 sm:before:flex-1">
      <div className="flex flex-wrap items-center gap-x-4 gap-y-2">
        <div className="flex items-center gap-x-2">
          {getEnvironmentBadge()}
          <span className="text-sm font-semibold text-white">
            Swaxi Dispo v{buildInfo.version}
          </span>
        </div>

        <div className="flex items-center gap-x-4 text-sm text-white/90">
          <span className="flex items-center gap-x-1">
            {getDataSourceIcon()}
            {buildInfo.dataSource === 'sharePoint' ? 'SharePoint' : 'Demo Modus'}
          </span>

          <span className="hidden sm:block">
            Build: {buildInfo.buildTime}
          </span>

          {buildInfo.gitHash !== 'dev' && (
            <span className="hidden md:block font-mono text-xs">
              #{buildInfo.gitHash.substring(0, 7)}
            </span>
          )}
        </div>

        {buildInfo.environment === 'production' && buildInfo.dataSource === 'localStorage' && (
          <div className="flex items-center gap-x-2 text-sm text-orange-200">
            <span className="animate-pulse">‚ö†Ô∏è</span>
            <span>Offline Modus - Daten werden lokal gespeichert</span>
          </div>
        )}
      </div>

      <div className="flex flex-1 justify-end">
        <button
          type="button"
          onClick={handleDismiss}
          className="flex-none rounded-md bg-white/10 p-1 text-white/80 hover:bg-white/20 hover:text-white"
          aria-label="Banner schlie√üen"
        >
          <span className="sr-only">Banner schlie√üen</span>
          <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
      </div>
    </div>
  );
}

import { NavLink } from 'react-router-dom'
// reference to satisfy strict unused var rule in certain test contexts
const _navLinkRef = NavLink
import { useContext } from 'react'
import AuthContext from '../contexts/AuthContext'

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function Navigation() {
  const auth = useContext(AuthContext)
  const role = auth?.user?.role
  const isAdmin = role === 'admin' || role === 'chief'
  const navigation = [
    { name: 'Dashboard', href: '/' },
    { name: 'Kalender', href: '/calendar' },
    isAdmin && { name: 'Verwaltung', href: '/admin' },
    isAdmin && { name: 'Audit', href: '/audit' },
    { name: 'üß™ Test', href: '/test' },
  ].filter(Boolean)

  return (
    <nav className="bg-brand-primary">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="flex h-16 justify-between">
          <div className="flex">
            <div className="flex flex-shrink-0 items-center">
              <span className="text-white font-bold text-xl">swaxi</span>
            </div>
            <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
              {navigation.map((item) => (
                <NavLink
                  key={item.name}
                  to={item.href}
                  end={item.href === '/'}
                  className={({ isActive }) =>
                    classNames(
                      isActive
                        ? 'border-brand-accent text-white'
                        : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-white',
                      'inline-flex items-center border-b-2 px-1 pt-1 text-sm font-medium'
                    )
                  }
                >
                  {item.name}
                </NavLink>
              ))}
              {!auth?.user && (
                <NavLink
                  to="/login"
                  className={({ isActive }) =>
                    classNames(
                      isActive ? 'border-brand-accent text-white' : 'border-transparent text-gray-300 hover:border-gray-300 hover:text-white',
                      'inline-flex items-center border-b-2 px-1 pt-1 text-sm font-medium'
                    )
                  }
                >Login</NavLink>
              )}
              {auth?.user && (
                <button
                  onClick={auth.logout}
                  className="inline-flex items-center border-b-2 border-transparent px-1 pt-1 text-sm font-medium text-gray-300 hover:text-white hover:border-gray-300"
                >Logout ({auth.user.role})</button>
              )}
            </div>
          </div>
        </div>
      </div>
    </nav>
  )
}

import { Fragment } from 'react'
import { Menu, Transition } from '@headlessui/react'
import { BellIcon } from '@heroicons/react/24/outline'
// linter reference
const _refs = [Menu, Transition, BellIcon]
import { useShifts } from '../contexts/useShifts'

export default function NotificationMenu() {
  const { state, markNotificationRead, markAllNotificationsRead } = useShifts();

  return (
    <Menu as="div" className="relative inline-block text-left">
      <Menu.Button className="flex items-center rounded-full bg-gray-50 p-1 text-gray-400 hover:text-gray-600">
        <span className="sr-only">View notifications</span>
        <BellIcon className="h-6 w-6" aria-hidden="true" />
        {state.notifications && state.notifications.length > 0 && (
          <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-red-400 ring-2 ring-white" />
        )}
      </Menu.Button>

      <Transition
        as={Fragment}
        enter="transition ease-out duration-100"
        enterFrom="transform opacity-0 scale-95"
        enterTo="transform opacity-100 scale-100"
        leave="transition ease-in duration-75"
        leaveFrom="transform opacity-100 scale-100"
        leaveTo="transform opacity-0 scale-95"
      >
        <Menu.Items className="absolute right-0 z-10 mt-2 w-80 origin-top-right rounded-md bg-white py-1 shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
          {state.notifications?.length > 0 && (
            <div className="px-3 py-1 flex justify-end border-b border-gray-100 text-xs">
              <button onClick={markAllNotificationsRead} className="text-blue-600 hover:underline">Alle gelesen</button>
            </div>
          )}
          {state.notifications && state.notifications.map((notification) => (
            <Menu.Item key={notification.id}>
              {({ active }) => (
                <div
                  className={`
                    ${active ? 'bg-gray-100' : ''}
                    px-4 py-2 text-sm text-gray-700
                  `}
                >
                  <p className="font-medium flex justify-between items-center">
                    <span>{notification.title}</span>
                    {!notification.isRead && (
                      <button onClick={() => markNotificationRead(notification.id)} className="ml-2 text-xs text-blue-600 hover:underline">Gelesen</button>
                    )}
                  </p>
                  <p className="text-gray-500">{notification.message}</p>
                  <p className="text-xs text-gray-400 mt-1 flex justify-between items-center">
                    <span>{notification.timestamp}</span>
                    {!notification.isRead && <span className="w-2 h-2 rounded-full bg-blue-500 inline-block" />}
                  </p>
                </div>
              )}
            </Menu.Item>
          ))}
          {(!state.notifications || state.notifications.length === 0) && (
            <div className="px-4 py-2 text-sm text-gray-500">
              Keine neuen Benachrichtigungen
            </div>
          )}
        </Menu.Items>
      </Transition>
    </Menu>
  )
}

import { useState } from 'react';
import { ROLES } from '../utils/constants';
import { canManageShifts } from '../utils/auth';

const roleDescriptions = {
  [ROLES.ADMIN]: 'Vollzugriff inkl. Audit-Log und Rollenverwaltung',
  [ROLES.CHIEF]: 'Dienste anlegen, zuweisen und Vorlagen bearbeiten',
  [ROLES.DISPONENT]: 'Dienste sehen und sich bewerben',
  [ROLES.ANALYST]: 'Nur Lesezugriff und Analytics',
};

export default function RoleManagement({ users, onUpdateRole }) {
  const [editingUser, setEditingUser] = useState(null);

  const handleRoleChange = (userId, newRole) => {
    onUpdateRole(userId, newRole);
    setEditingUser(null);
  };

  const getRoleBadgeColor = (role) => {
    switch (role) {
      case ROLES.ADMIN:
        return 'bg-purple-100 text-purple-800';
      case ROLES.CHIEF:
        return 'bg-blue-100 text-blue-800';
      case ROLES.DISPONENT:
        return 'bg-green-100 text-green-800';
      case ROLES.ANALYST:
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="bg-white shadow overflow-hidden sm:rounded-md">
      <ul className="divide-y divide-gray-200">
        {users.map((user) => (
          <li key={user.id}>
            <div className="px-4 py-4 sm:px-6">
              <div className="flex items-center justify-between">
                <div className="text-sm font-medium text-brand-primary">
                  {user.name}
                </div>
                <div className="ml-2 flex-shrink-0 flex items-center space-x-2">
                  {editingUser === user.id ? (
                    <select
                      value={user.role}
                      onChange={(e) => handleRoleChange(user.id, e.target.value)}
                      className="block w-full rounded-md border-0 py-1.5 pl-3 pr-10 text-gray-900 ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-brand-primary sm:text-sm sm:leading-6"
                    >
                      {Object.values(ROLES).map((role) => (
                        <option key={role} value={role}>
                          {role}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <>
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getRoleBadgeColor(user.role)}`}>
                        {user.role}
                      </span>
                      {canManageShifts(user.role) && (
                        <button
                          onClick={() => setEditingUser(user.id)}
                          className="text-sm text-brand-primary hover:text-brand-primary/80"
                        >
                          Bearbeiten
                        </button>
                      )}
                    </>
                  )}
                </div>
              </div>
              <div className="mt-2 text-sm text-gray-500">
                {roleDescriptions[user.role]}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

import { useState } from 'react';
import { useShifts } from '../contexts/useShifts';
