import { useState, useRef, useEffect } from 'react';

import { useShifts } from '../contexts/useShifts';
import { useAuth } from '../contexts/useAuth';
import { SHIFT_STATUS } from '../utils/constants';

export default function SeriesApplicationModal({ isOpen, onClose, shifts = [] }) {
  const { applyToSeries } = useShifts();
  const auth = useAuth(); // may be undefined in isolated tests
  const [selectedShifts, setSelectedShifts] = useState([]);
  // Preserve legacy default "current-user" to keep existing tests stable
  const [userId] = useState(auth?.user?.id || 'current-user');

  // Focus management: save last active element when modal opens, restore when closes
  useEffect(() => {
    if (isOpen) {
      lastActiveRef.current = document.activeElement;
    }
    // When modal closes, restore focus to last active element
    if (!isOpen && lastActiveRef.current) {
      // Use setTimeout to ensure focus after modal unmount
      setTimeout(() => {
        if (lastActiveRef.current && typeof lastActiveRef.current.focus === 'function') {
          lastActiveRef.current.focus();
        }
      }, 0);
    }
  }, [isOpen]);
  const availableShifts = shifts.filter(shift => shift.status === SHIFT_STATUS.OPEN);

  const handleShiftToggle = (shiftId) => {
    setSelectedShifts(prev => 
      prev.includes(shiftId) 
        ? prev.filter(id => id !== shiftId)
        : [...prev, shiftId]
    );
  };

  const handleSubmit = () => {
    if (selectedShifts.length > 0) {
      applyToSeries(selectedShifts, userId);
      onClose();
      setSelectedShifts([]);
    }
  };

  const selectAllSameType = (type) => {
    const sameTypeShifts = availableShifts
      .filter(shift => shift.type === type)
      .map(shift => shift.id);
    setSelectedShifts(prev => [...new Set([...prev, ...sameTypeShifts])]);
  };

  const selectAllWeekdays = () => {
    const weekdayShifts = availableShifts
      .filter(shift => {
        const date = new Date(shift.date);
        const day = date.getDay();
        return day >= 1 && day <= 4; // Monday to Thursday
      })
      .map(shift => shift.id);
    setSelectedShifts(prev => [...new Set([...prev, ...weekdayShifts])]);
  };

  const selectAllWeekends = () => {
    const weekendShifts = availableShifts
      .filter(shift => {
        const date = new Date(shift.date);
        const day = date.getDay();
        return day === 0 || day === 5 || day === 6; // Friday, Saturday, Sunday
      })
      .map(shift => shift.id);
    setSelectedShifts(prev => [...new Set([...prev, ...weekendShifts])]);
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50" role="dialog" aria-modal="true" aria-labelledby="series-modal-title" aria-describedby="series-modal-desc">
      <div className="relative top-20 mx-auto p-5 border w-11/12 md:w-3/4 lg:w-1/2 shadow-lg rounded-md bg-white" role="document">
        <div className="mt-3">
          <div className="flex items-center justify-between mb-4">
            <h3 id="series-modal-title" className="text-lg font-medium text-gray-900">
              Serienbewerbung
            </h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <span className="sr-only">Schließen</span>
              ✕
            </button>
          </div>

          <div className="mb-4">
            <p id="series-modal-desc" className="text-sm text-gray-600 mb-3">
              Bewerben Sie sich für mehrere Dienste gleichzeitig. Wählen Sie die gewünschten Dienste aus oder nutzen Sie die Schnellauswahl.
            </p>

            <div className="flex flex-wrap gap-2 mb-4">
              <button
                onClick={() => selectAllSameType('evening')}
                className="px-3 py-1 text-xs bg-blue-100 text-blue-800 rounded-full hover:bg-blue-200"
              >
                Alle Abend-Dienste
              </button>
              <button
                onClick={() => selectAllSameType('night')}
                className="px-3 py-1 text-xs bg-purple-100 text-purple-800 rounded-full hover:bg-purple-200"
              >
                Alle Nacht-Dienste
              </button>
              <button
                onClick={() => selectAllSameType('early')}
                className="px-3 py-1 text-xs bg-green-100 text-green-800 rounded-full hover:bg-green-200"
              >
                Alle Früh-Dienste
              </button>
              <button
                onClick={selectAllWeekdays}
                className="px-3 py-1 text-xs bg-gray-100 text-gray-800 rounded-full hover:bg-gray-200"
              >
                Mo-Do
              </button>
              <button
                onClick={selectAllWeekends}
                className="px-3 py-1 text-xs bg-orange-100 text-orange-800 rounded-full hover:bg-orange-200"
              >
                Fr-So
              </button>
            </div>
          </div>

          <div className="max-h-60 overflow-y-auto border border-gray-200 rounded-md">
            {availableShifts.length === 0 ? (
              <p className="text-center text-gray-500 py-4">Keine offenen Dienste verfügbar</p>
            ) : (
              <ul className="divide-y divide-gray-200">
                {availableShifts.map((shift) => (
                  <li key={shift.id} className="p-3">
                    <label className="flex items-center">
                      <input
                        type="checkbox"
                        checked={selectedShifts.includes(shift.id)}
                        onChange={() => handleShiftToggle(shift.id)}
                        className="rounded border-gray-300 text-[var(--color-primary)] focus:ring-[var(--color-primary)]"
                      />
                      <div className="ml-3 flex-1">
                        <div className="text-sm font-medium text-gray-900">
                          {shift.date instanceof Date 
                            ? shift.date.toLocaleDateString('de-DE', { 
                                weekday: 'short', 
                                day: '2-digit', 
                                month: '2-digit' 
                              }) 
                            : shift.date}
                        </div>
                        <div className="text-sm text-gray-500">
                          {shift.type} • {shift.start}-{shift.end}
                        </div>
                      </div>
                    </label>
                  </li>
                ))}
              </ul>
            )}
          </div>

          <div className="mt-6 flex justify-between items-center">
            <p className="text-sm text-gray-600">
              {selectedShifts.length} Dienst{selectedShifts.length !== 1 ? 'e' : ''} ausgewählt
            </p>
            <div className="flex space-x-3">
              <button
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Abbrechen
              </button>
              <button
                onClick={handleSubmit}
                disabled={selectedShifts.length === 0}
                className="btn btn-primary px-4 py-2 text-sm disabled:bg-gray-300 disabled:cursor-not-allowed"
              >
                Bewerben
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

import { render, screen, fireEvent, waitFor } from '@testing-library/react'
import React from 'react'

import SeriesApplicationModal from '../../components/SeriesApplicationModal'
import { renderWithProviders } from '../testUtils'
import { SHIFT_STATUS } from '../../utils/constants'
import * as useShiftsModule from '../../contexts/useShifts'

// Mock useShifts to observe applyToSeries calls without full provider complexity
const mockApplyToSeries = jest.fn()

jest.spyOn(useShiftsModule, 'useShifts').mockImplementation(() => ({ applyToSeries: mockApplyToSeries }))

function makeShift(id, dateStr, type='evening') {
  return { id, date: new Date(dateStr), type, start: '18:00', end: '20:00', status: SHIFT_STATUS.OPEN }
}

describe('SeriesApplicationModal focus management', () => {
  let triggerButton
  let onClose

  beforeEach(() => {
    onClose = jest.fn()
    mockApplyToSeries.mockClear()
    // Create a trigger button to test focus restoration
    triggerButton = document.createElement('button')
    triggerButton.textContent = 'Open Series Application'
    document.body.appendChild(triggerButton)
    triggerButton.focus()
  })

  afterEach(() => {
    if (triggerButton) {
      document.body.removeChild(triggerButton)
    }
  })

  it('has correct accessibility attributes', () => {
    const shifts = [makeShift('s1', '2025-08-25', 'evening')]
    renderWithProviders(
      <SeriesApplicationModal isOpen={true} onClose={onClose} shifts={shifts} />
    )

    // Check dialog role exists
    const dialogElement = document.querySelector('[role="dialog"]')
    expect(dialogElement).toBeInTheDocument()
    expect(dialogElement).toHaveAttribute('aria-labelledby', 'series-modal-title')
    expect(dialogElement).toHaveAttribute('aria-describedby', 'series-modal-desc')
    expect(dialogElement).toHaveAttribute('aria-modal', 'true')

    // Check title and description elements exist
    expect(screen.getByText('Serienbewerbung')).toHaveAttribute('id', 'series-modal-title')
    expect(screen.getByText(/Bewerben Sie sich für mehrere Dienste/)).toHaveAttribute('id', 'series-modal-desc')
  })

  it('stores focused element when modal opens', () => {
    const shifts = [makeShift('s1', '2025-08-25', 'evening')]

    // Focus the trigger button before opening modal
    triggerButton.focus()
    expect(document.activeElement).toBe(triggerButton)

    renderWithProviders(
      <SeriesApplicationModal isOpen={true} onClose={onClose} shifts={shifts} />
    )

    // Modal should be open and have proper aria attributes
    const dialog = document.querySelector('[role="dialog"]')
    expect(dialog).toBeInTheDocument()
  })

  it('restores focus to trigger element when modal closes', async () => {
    const shifts = [makeShift('s1', '2025-08-25', 'evening')]

    // Focus the trigger button before opening modal
    triggerButton.focus()
    expect(document.activeElement).toBe(triggerButton)

    const { rerender } = renderWithProviders(
      <SeriesApplicationModal isOpen={true} onClose={onClose} shifts={shifts} />
    )

    // Modal should be open and have dialog role
    expect(document.querySelector('[role="dialog"]')).toBeInTheDocument()

    // Close the modal by rerendering with isOpen=false
    rerender(
      <div>
        <SeriesApplicationModal isOpen={false} onClose={onClose} shifts={shifts} />
      </div>
    )

    // Focus should be restored to trigger button
    await waitFor(() => {
      expect(document.activeElement).toBe(triggerButton)
    })
  })

  it('focus remains trapped within modal during interaction', () => {
    const shifts = [makeShift('s1', '2025-08-25', 'evening')]

    renderWithProviders(
      <SeriesApplicationModal isOpen={true} onClose={onClose} shifts={shifts} />
    )

    const dialog = document.querySelector('[role="dialog"]')
    expect(dialog).toBeInTheDocument()

    // Find focusable elements in the modal
    const checkboxes = screen.getAllByRole('checkbox')
    const buttons = screen.getAllByRole('button')

    expect(checkboxes.length).toBeGreaterThan(0)
    expect(buttons.length).toBeGreaterThan(0)

    // Focus should be contained within the modal
    checkboxes[0].focus()
    expect(dialog.contains(document.activeElement)).toBe(true)
  })

  it('returns focus to trigger button when closed via close button', async () => {
    const shifts = [makeShift('s1', '2025-08-25', 'evening')]

    // Focus the trigger button before opening modal
    triggerButton.focus()
    expect(document.activeElement).toBe(triggerButton)

    renderWithProviders(
      <SeriesApplicationModal isOpen={true} onClose={onClose} shifts={shifts} />
    )

    // Modal should be open
    expect(document.querySelector('[role="dialog"]')).toBeInTheDocument()

    // Click the close button (with sr-only text "Schließen")
    const closeButton = screen.getByRole('button', { name: /Schließen/i })
    fireEvent.click(closeButton)

    // onClose should have been called
    expect(onClose).toHaveBeenCalled()
  })
})
